item.init:
let uses = 10, dmg = 25;
function unpack() {
	user.addItem("Grenade", uses);
}

item.use:
if(canUse) {
	print("You throw a grenade!");
	let i = 0;
	for(i < targets.length) {
		targets[i].damage(dmg);
		i++;
	}
	used();
}
