item.use:

	if(canUse) {
		print("You throw a grenade!");
		for(let i = 0; i < targets.length; i++) {
			targets[i].damage(dmg);
		}
		used();
	}

item.init:
log("POWER ARMOR: Trying to add auras...");
addAura("Strength", 10);
addAura("Intelligence", 3);
addAura("Defense", 5);

item.use:
if(canUse) {
	print("!!WARNING!!: Suit does not contain Bubblegum");
}

if(canUse) {
	write('You throw a grenade!\nTargets:');
	for(var i = 0; i < targets.length; i++) {
		write(targets[i].getName() + '\n');
	}
	for(var i = 0; i < targets.length; i++) {
		write(targets[i].getName() + ' takes ' + dmg + ' damage!\n');
		targets[i].damage(dmg);
	}
	used();
}

item.init:
setUses(10);
var dmg = 25;
var unpack = function() {
	user.addItem("Grenade", uses);
	used();
}

item.use:
if(canUse()) {
	write('You throw a grenade!\nTargets:');
	if(targets.length > 0) {
		for(var i = 0; i < targets.length; i++) {
			write(targets[i].getName() + '\n');
		}
		for(var i = 0; i < targets.length; i++) {
			write(targets[i].getName() + ' takes ' + dmg + ' damage!\n');
			targets[i].damage(dmg);
		}
		used();
	} else {
		write('No targets!\n');
	}
}

if(canUse) {
	write(“You charge forward!”);
	if(targets.length > 0) {
		for(var i = 0; i < targets.length; i++) {
			targets[i].damage(dmg);
		}
	} else {
		write(“But had no direction, and hit a wall.”);

	}
	used();
}
